import sqlalchemy as sa

engine = sa.create_engine('sqlite:///books.db')
conn = engine.connect()
sql = sa.text('SELECT title FROM books ORDER BY title')
rows = conn.execute(sql)

results_as_dict = rows.mappings().all()
print(results_as_dict)

for row in rows:
    print('title:', row[0])

conn.close()
engine.dispose()